{-# LANGUAGE DerivingStrategies #-}
{-# LANGUAGE DeriveAnyClass #-}

module Images where 

import qualified Data.Text as T
import qualified Data.Text.IO as TIO
-- import qualified Data.Text.Encoding as E
import Data.Aeson (eitherDecode, encode, ToJSON(..), FromJSON (..))
import GHC.Generics (Generic)
-- import qualified Data.ByteString.Lazy.Char8 as BC
-- import qualified Data.ByteString.Lazy as L 
import qualified Data.ByteString as B 
import Data.ByteString.Base64


data Image = MkImage 
  { iId :: Int
  , iHeader :: T.Text
  , iBase64 :: T.Text
  } deriving stock (Show, Generic)
    deriving anyclass (ToJSON, FromJSON)

loadImage :: IO ()
loadImage =  do 
  -- content <- B.readFile "config/imageTest.png"
  content <- B.readFile "config/smallImage.png"
  let base64 = encodeBase64 content
  TIO.writeFile "config/base64.txt" base64
  putStrLn "Content:"
  -- B.putStr content
  TIO.putStrLn base64

testImage :: Image
testImage = MkImage 1 "image/png" ""

